import React from 'react'
import { Component } from 'react'
import { Card, Button, Form } from 'react-bootstrap'

const API_KEY = "886705b4c1182eb1c69f28eb8c520e20"

class mainPage extends Component {

  WetherInf = async (event) => {
    event.preventDefoult();
      let city = event.target.elements.city.value
      const api_url = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`)
      const data = await api_url.json();
      console.log(data)

  }

  render() {
    return(
      <Card style={{width: '18prem'}} className="card-body">
        <Card.Body>
          <Card.Title>City</Card.Title>
          <Form onSubmit={this.props.WetherInf}>
          <Card.Text>
            <input type="text" name="city" placeholder="City" />
            <button onClick={this.props.WetherInf}>Do it</button>
          </Card.Text>
          <Button variant="primary" onClick={this.props.WetherInf}>Go</Button>
          </Form>
        </Card.Body>
      </Card>

      // <div>
      //   <form onSubmit={this.props.WetherInf}>
      //        <input type="text" name="city" placeholder="City" />
      //         <button>Go</button>

      //   </form>
      // </div>
    )
  }
}

export default mainPage
